package com.example.recyclerlessons.staggered

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.StaggeredGridLayoutManager
import com.example.recyclerlessons.R
import kotlinx.android.synthetic.main.activity_straggered_recycler.*

class StaggeredRecyclerActivity : AppCompatActivity() {
    private val NUM_COLUMNS = 2

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_straggered_recycler)

        initRV()
    }

    private fun initRV() {
        val adapter = StagAdapter()
        adapter.updateStag(createStagDatas)

        my_recycler_view.layoutManager = StaggeredGridLayoutManager(NUM_COLUMNS,LinearLayoutManager.VERTICAL)
        my_recycler_view.adapter = adapter
    }

    private val createStagDatas = ArrayList<StagData>().also {
